import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {
  getCart,
  removeFromCart,
  clearCart,
  checkoutCart,
  resetCart
} from '../slices/cartSlice';
import { fetchProducts } from '../slices/productSlice';
import { placeOrder } from '../slices/orderSlice';
import { useNavigate } from 'react-router-dom';

const Cart = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { items, loading, error, checkedOut } = useSelector((state) => state.cart);
  const { list: products } = useSelector((state) => state.products);
  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!token) return navigate('/login');

    const userId = JSON.parse(atob(token.split('.')[1])).userId;
    dispatch(getCart(userId));
    dispatch(fetchProducts());
  }, [dispatch, token, navigate]);


  const handleRemove = (productId) => {
    const userId = JSON.parse(atob(token.split('.')[1])).userId;
    dispatch(removeFromCart({ userId, productId }));
  };

  const handleClear = () => {
    const userId = JSON.parse(atob(token.split('.')[1])).userId;
    dispatch(clearCart(userId));
  };

  const handleCheckout = () => {
    const userId = JSON.parse(atob(token.split('.')[1])).userId;

    // Get cart items from Redux state
    const cartItems = items.map(({ productId, quantity }) => ({
      productId,
      quantity,
    }));

    // Dispatch placeOrder first
    dispatch(placeOrder({ userId, token, items: cartItems }))
      .unwrap()
      .then(() => {
        dispatch(checkoutCart(userId));
        navigate('/orders');
      })
      .catch((err) => {
        console.error('Order placement failed:', err);
      });
  };

  if (loading) return <p>Loading cart...</p>;
  if (error) return <p>Error: {error}</p>;

  // Merge cart items with product details
  const mergedCartItems = items.map((item) => {
    const product = products.find((p) => p._id === item.productId);
    return {
      ...item,
      product,
    };
  });

  return (
    <div style={{ padding: '1rem' }}>
      <h2>Your Cart</h2>
      {mergedCartItems.length === 0 ? (
        <p>No items in cart.</p>
      ) : (
        <>
          <ul style={{ listStyle: 'none', padding: 0 }}>
            {mergedCartItems.map((item) => (
              <li key={item._id} style={{ marginBottom: '1rem', border: '1px solid #ddd', padding: '1rem' }}>
                <h4>{item.product?.name || 'Unknown Product'}</h4>
                <p>Price: â‚¹{item.product?.price || 'N/A'}</p>
                <p>Quantity: {item.quantity}</p>
                <button onClick={() => handleRemove(item.productId)}>Remove</button>
              </li>
            ))}
          </ul>
          <div style={{ marginTop: '1rem' }}>
            <button onClick={handleClear} style={{ marginRight: '1rem' }}>Clear Cart</button>
            <button onClick={handleCheckout}>Checkout</button>
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
