import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchOrders } from '../slices/orderSlice';
import { resetCart } from '../slices/cartSlice';

import { useNavigate } from 'react-router-dom';

const Orders = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { list: orders, loading, error } = useSelector((state) => state.orders);

  const token = localStorage.getItem('token');

  useEffect(() => {
    const timer = setTimeout(() => {
      dispatch(resetCart());
    }, 3000);
    return () => clearTimeout(timer);
  }, [dispatch]);

  useEffect(() => {
    if (!token) return navigate('/login');

    const userId = JSON.parse(atob(token.split('.')[1])).userId;
    dispatch(fetchOrders({ userId, token }));
  }, [dispatch, token, navigate]);

  if (loading) return <p>Loading orders...</p>;
  if (error) return <p>Error: {error}</p>;

  return (
    <div style={{ padding: '1rem' }}>
      <h2>Your Orders</h2>
      {orders.length === 0 ? (
        <p>No orders placed yet.</p>
      ) : (
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {orders?.map((order) => (
            <li key={order._id} style={{ border: '1px solid #ccc', marginBottom: '1rem', padding: '1rem' }}>
              <h4>Order ID: {order._id}</h4>
              <ul>
                {order.items.map((item, index) => (
                  <li key={index}>
                    <p>Product ID: {item.productId}</p>
                    <p>Quantity: {item.quantity}</p>

                  </li>
                ))}
              </ul>
              <h4>Status: {order.status}</h4>
              <p>Placed On: {new Date(order.placedAt).toLocaleString()}</p>
              <p>Shipped On: {new Date(order.shippedAt).toLocaleString() || 'Pending'}</p>
              <p>Delivered On: {new Date(order.deliveredAt).toLocaleString() || 'Pending'}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Orders;
